VertexShader:
#version 300 es

layout(location = 0) in vec3 Position;
out vec2 vdspv_fsin0;
layout(location = 1) in vec2 UV0;
out vec4 vdspv_fsin1;
layout(location = 2) in vec4 Color;

void main()
{
    gl_Position = vec4(Position, 1.0);
    vdspv_fsin0 = UV0;
    vdspv_fsin1 = Color;
}

FragmentShader:
#version 300 es
precision mediump float;
precision highp int;

uniform highp sampler2D DiffuseTexture;

in highp vec2 vdspv_fsin0;
layout(location = 0) out highp vec4 FragColor;
in highp vec4 vdspv_fsin1;

void main()
{
    highp vec4 diffuseColor = texture(DiffuseTexture, vdspv_fsin0);
    FragColor = diffuseColor;
}
